import React from 'react';
import { useSortable } from "@dnd-kit/sortable";
import { CSS } from "@dnd-kit/utilities";
import {useState} from 'react'
import editIcon from '../../assets/edit.png'
import trashOpen from '../../assets/trash-open.jpg'
import trashClose from '../../assets/trash-close.jpg'
import "./todoItem.scss"

export const TodoItem = (props) => {
    const {todo} = props

    const [title, setTitle] = useState(todo.title)
    const [description, setDescription] = useState(todo.description)

    const [trashIcon, setTrashIcon] = useState(trashClose)

    const {
        attributes,
        listeners,
        setNodeRef,
        transform,
        transition
    } = useSortable({ id: todo.id });

    const style = {
        transform: CSS.Transform.toString(transform),
        transition
    };

    function handleEdit(id) {
        console.log("edit handle", id)
    }
    function handleDelete(id) {
        console.log("handle delete", id)
    }

    return (
        <div ref={setNodeRef} style={style}>
            <div class="todo-item-container">
                <div class="todo-item" {...attributes} {...listeners}>
                    <p>{title}</p>    
                    <p>{description}</p>    
                </div>        
                <div class="todo-options">
                    {/* <div onClick={() => handleEdit(todo.id)} className="edit-btn" alt="edit" > </div> */}
                    <img 
                            onClick={() => handleDelete(todo.id)} 
                            onMouseEnter={() => setTrashIcon(trashClose)}
                            onMouseLeave={() => setTrashIcon(trashOpen)}
                            className="edit-btn" src={trashIcon} alt="edit" />
                    <img onClick={() => handleEdit} className="edit-btn" src={editIcon} alt="edit" />  
                </div>        
            </div>
        </div>
    );
}
